 12	// ResponseDecoder 响应体解码器
 13	type ResponseDecoder struct{}
 14	
 15	// NewResponseDecoder 创建响应体解码器
 16	func NewResponseDecoder() *ResponseDecoder {
 17		return &ResponseDecoder{}
 18	}
 19	
 20	// DecodeResponse 解码响应体
 21	func (rd *ResponseDecoder) DecodeResponse(response *FlowResponse) error {
 22		if response == nil || len(response.Body) == 0 {
 23			return nil
 24		}
 25	
 26		// 获取内容编码
 27		encoding := strings.ToLower(response.Headers["Content-Encoding"])
 28		contentType := strings.ToLower(response.Headers["Content-Type"])
 29		
 30		// 设置基本信息
 31		response.ContentType = contentType
 32		response.Encoding = encoding
 33	
 34		// 解码压缩内容
 35		decodedBody, err := rd.decompressBody(response.Body, encoding)
 36		if err != nil {
 37			// 解码失败，使用原始内容
 38			decodedBody = response.Body
 39		}
 40		response.DecodedBody = decodedBody
 41	
 42		// 判断内容类型
 43		response.IsText = rd.isTextContent(decodedBody, contentType)
 44		response.IsBinary = !response.IsText
 45	
 46		// 生成16进制视图（对于二进制内容或大文件）
 47		if response.IsBinary || len(decodedBody) > 1024*1024 { // 大于1MB的文件
 48			response.HexView = rd.generateHexView(decodedBody)
 49		}
 50	
 51		return nil
 52	}
 53	
 54	// decompressBody 解压响应体
 55	func (rd *ResponseDecoder) decompressBody(body []byte, encoding string) ([]byte, error) {
 56		switch encoding {
 57		case "gzip":
 58			return rd.decompressGzip(body)
 59		case "deflate":
 60			return rd.decompressDeflate(body)
 61		case "br":
 62			return rd.decompressBrotli(body)
 63		default:
 64			// 尝试自动检测压缩格式
 65			if decompressed, err := rd.autoDetectAndDecompress(body); err == nil {
 66				return decompressed, nil
 67			}
 68			return body, nil
 69		}
 70	}
 71	
 72	// decompressGzip 解压Gzip
 73	func (rd *ResponseDecoder) decompressGzip(data []byte) ([]byte, error) {
 74		reader, err := gzip.NewReader(bytes.NewReader(data))
 75		if err != nil {
 76			return nil, err
 77		}
 78		defer reader.Close()
 79	
 80		return io.ReadAll(reader)
 81	}
 82	
 83	// decompressDeflate 解压Deflate
 84	func (rd *ResponseDecoder) decompressDeflate(data []byte) ([]byte, error) {
 85		// 简单的deflate解压实现
 86		// 注意：这里可能需要更复杂的实现来处理不同的deflate格式
 87		return data, fmt.Errorf("deflate解压暂未实现")
 88	}
