 114	// isTextContent 判断是否为文本内容
 115	func (rd *ResponseDecoder) isTextContent(data []byte, contentType string) bool {
 116		// 根据Content-Type判断
 117		if strings.Contains(contentType, "text/") ||
 118			strings.Contains(contentType, "application/json") ||
 119			strings.Contains(contentType, "application/xml") ||
 120			strings.Contains(contentType, "application/javascript") ||
 121			strings.Contains(contentType, "application/x-javascript") ||
 122			strings.Contains(contentType, "text/javascript") ||
 123			strings.Contains(contentType, "application/xhtml+xml") ||
 124			strings.Contains(contentType, "application/rss+xml") ||
 125			strings.Contains(contentType, "application/atom+xml") {
 126			return true
 127		}
 128	
 129		// 如果Content-Type不明确，通过内容判断
 130		if len(data) == 0 {
 131			return true
 132		}
...
 175	
 176		for line := 0; line < lines; line++ {
 177			offset := line * bytesPerLine
 178			
 179			// 写入偏移地址
 180			result.WriteString(fmt.Sprintf("%08x ", offset))
 181	
 182			// 写入16进制数据
 183			for i := 0; i < bytesPerLine; i++ {
 184				if offset+i < len(data) {
 185					result.WriteString(fmt.Sprintf("%02x ", data[offset+i]))
 186				} else {
 187					result.WriteString(" ")
 188				}
 189	
 190				// 在第8个字节后添加额外空格
 191				if i == 7 {
 192					result.WriteString(" ")
 193				}
 194			}
 195	
 196			// 写入ASCII表示
 197			result.WriteString(" |")
 198			for i := 0; i < bytesPerLine && offset+i < len(data); i++ {
 199				b := data[offset+i]
 200				if b >= 32 && b <= 126 {
 201					result.WriteByte(b)
 202				} else {
 203					result.WriteByte('.')
 204				}
 205			}
 206			result.WriteString("|\n")
 207		}
 208	
 209		// 如果内容被截断，添加提示
 210		if len(data) > maxLines*bytesPerLine {
 211			result.WriteString(fmt.Sprintf("\n... (显示前 %d 行，总共 %d 字节)\n", maxLines, len(data)))
 212		}
 213	
 214		return result.String()
 215	}
 216	
 217	// GetContentSummary 获取内容摘要
 218	func (rd *ResponseDecoder) GetContentSummary(response *FlowResponse) string {
 219		if response == nil {
 220			return "无响应"
 221		}
 222	
 223		if len(response.Body) == 0 {
 224			return "空响应"
 225		}
 226	
 227		if response.IsText {
 228			textLen := len(response.DecodedBody)
 229			if textLen > 100 {
 230				preview := string(response.DecodedBody[:100])
 231				return fmt.Sprintf("文本内容 (%d 字节): %s...", textLen, preview)
 232			}
 233			return fmt.Sprintf("文本内容 (%d 字节): %s", textLen, string(response.DecodedBody))
 234		}
 235	
 236		return fmt.Sprintf("二进制内容 (%d 字节)", len(response.Body))
 237	}
