 53	// FlowResponse 表示HTTP响应
 54	type FlowResponse struct {
 55		StatusCode int `json:"statusCode"`
 56		Status string `json:"status"`
 57		Headers map[string]string `json:"headers"`
 58		Body []byte `json:"body"` // 原始响应体
 59		DecodedBody []byte `json:"decodedBody"` // 解码后的响应体
 60		HexView string `json:"hexView"` // 16进制视图
 61		IsText bool `json:"isText"` // 是否为文本内容
 62		IsBinary bool `json:"isBinary"` // 是否为二进制内容
 63		ContentType string `json:"contentType"` // 内容类型
 64		Encoding string `json:"encoding"` // 编码方式
 65		Raw string `json:"raw"`
 66	}
