 22	// InterceptRequest 拦截请求
 23	func (mli *MapLocalInterceptor) InterceptRequest(flow *proxycore.Flow, w http.ResponseWriter, r *http.Request) (bool, error) {
 24		rule, err := mli.manager.MatchRule(flow.URL)
 25		if err != nil {
 26			return false, err
 27		}
 28		
 29		if rule != nil {
 30			// 处理Map Local
 31			err = mli.manager.HandleMapLocal(w, r, rule)
 32			if err != nil {
 33				return false, err
 34			}
 35			
 36			// 标记Flow为Map Local处理
 37			flow.AddTag("map-local")
 38			return true, nil
 39		}
 40		
 41		return false, nil
 42	}
