 57	// SetEventHandler 设置事件处理器
 58	func (bi *BreakpointInterceptor) SetEventHandler(handler func(session *BreakpointSession)) {
 59		bi.eventHandler = handler
 60		bi.manager.SetEventHandler(handler)
 61	}
 62	
 63	// InterceptRequest 拦截请求
 64	func (bi *BreakpointInterceptor) InterceptRequest(flow *proxycore.Flow, w http.ResponseWriter, r *http.Request) (bool, error) {
 65		session, hasBreakpoint := bi.manager.CheckBreakpoint(flow, "request")
 66		if !hasBreakpoint {
 67			return false, nil
 68		}
 69		
 70		// 等待断点恢复
 71		_, err := bi.manager.WaitForBreakpoint(session, 5*time.Minute)
 72		if err != nil {
 73			return false, err
 74		}
 75		
 76		// 如果有修改的请求，使用修改后的请求
 77		if session.ModifiedRequest != nil {
 78			*r = *session.ModifiedRequest
 79		}
 80		
 81		flow.AddTag("breakpoint-request")
 82		return false, nil // 继续处理请求
 83	}
 84	
 85	// InterceptResponse 拦截响应
 86	func (bi *BreakpointInterceptor) InterceptResponse(flow *proxycore.Flow, resp *http.Response) (*http.Response, error) {
 87		session, hasBreakpoint := bi.manager.CheckBreakpoint(flow, "response")
 88		if !hasBreakpoint {
 89			return resp, nil
 90		}
 91		
 92		// 等待断点恢复
 93		modifiedResp, err := bi.manager.WaitForBreakpoint(session, 5*time.Minute)
 94		if err != nil {
 95			return resp, err
 96		}
 97		
 98		flow.AddTag("breakpoint-response")
 99	
 100		if modifiedResp != nil {
 101			return modifiedResp, nil
 102		}
 103	
 104		return resp, nil
 105	}
