 440	// Shutdown 应用关闭时的清理工作
 441	func (a *App) Shutdown(ctx context.Context) {
 442		// 停止代理
 443		if a.isRunning {
 444			a.StopProxy()
 445		}
 446	
 447		// 保存配置
 448		if err := a.config.SaveConfig(); err != nil {
 449			logger.Error("Failed to save config: %v", err)
 450		}
 451	
 452		// 关闭日志
 453		logger.Close()
 454	}
 455	
 456	// ExportFlows 导出流量数据
 457	func (a *App) ExportFlows(options export.ExportOptions) (*export.ExportResult, error) {
 458		logger.Info("Starting export with type: %s, scope: %s, flows count: %d",
 459			options.Type, options.Scope, len(options.Flows))
 460	
 461		result, zipData, err := a.exportService.ExportToZip(options)
 462		if err != nil {
 463			logger.Error("Export failed: %v", err)
 464			return nil, err
 465		}
 466	
 467		if result.Success && len(zipData) > 0 {
 468			// 使用Wails的文件保存对话框
 469			filename, err := runtime.SaveFileDialog(a.ctx, runtime.SaveDialogOptions{
 470				DefaultFilename: options.Filename,
 471				Title: "保存导出文件",
 472				Filters: []runtime.FileFilter{
 473					{
 474						DisplayName: "ZIP文件",
 475						Pattern: "*.zip",
 476					},
 477				},
 478			})
 479	
 480			if err != nil {
 481				logger.Error("Failed to show save dialog: %v", err)
 482				return nil, fmt.Errorf("文件保存对话框失败: %w", err)
 483			}
 484	
 485			if filename == "" {
 486				// 用户取消了保存
 487				result.Success = false
 488				result.Message = "用户取消了保存"
 489				return result, nil
 490			}
 491	
 492			// 写入文件
 493			if err := a.systemManager.WriteFile(filename, zipData); err != nil {
 494				logger.Error("Failed to write export file: %v", err)
 495				return nil, fmt.Errorf("文件写入失败: %w", err)
 496			}
 497	
 498			result.Filename = filename
 499			logger.Info("Export completed successfully: %s", filename)
 500		}
 501	
 502		return result, nil
 503	}
