 515	// GetResponseHexView 获取响应体的16进制视图
 516	func (a *App) GetResponseHexView(flowID string) (string, error) {
 517		flow, exists := a.proxyServer.GetFlow(flowID)
 518		if !exists {
 519			return "", fmt.Errorf("flow not found: %s", flowID)
 520		}
 521	
 522		if flow.Response == nil {
 523			return "", fmt.Errorf("no response data")
 524		}
 525	
 526		return flow.Response.HexView, nil
 527	}
