 110		f.Duration = f.EndTime.Sub(f.StartTime)
 111		f.StatusCode = resp.StatusCode
 112		f.ResponseSize = int64(len(body))
 113	
 114		f.Response = &FlowResponse{
 115			StatusCode: resp.StatusCode,
 116			Status: resp.Status,
 117			Headers: make(map[string]string),
 118			Body: body,
 119		}
 120	
 121		// 复制响应头
 122		for name, values := range resp.Header {
 123			if len(values) > 0 {
 124				f.Response.Headers[name] = values[0]
 125			}
 126		}
 127	
 128		// 更新内容类型（如果响应中有）
 129		if contentType := resp.Header.Get("Content-Type"); contentType != "" && f.ContentType == "" {
 130			f.ContentType = contentType
 131		}
 132	
 133		// 自动解码响应体
 134		decoder := NewResponseDecoder()
 135		if err := decoder.DecodeResponse(f.Response); err != nil {
 136			// 解码失败，记录错误但不影响正常流程
 137			fmt.Printf("Failed to decode response for %s: %v\n", f.URL, err)
 138		}
 139	}
