 5	echo " 启动 ProxyWoman 开发环境..."
 6	
 7	# 检查是否安装了必要的工具
 8	if ! command -v go &> /dev/null; then
 9	 echo "❌ Go 未安装，请先安装 Go 1.23+"
 10	 exit 1
 11	fi
 12	
 13	if ! command -v wails &> /dev/null; then
 14	 echo "❌ Wails CLI 未安装，正在安装..."
 15	 go install github.com/wailsapp/wails/v2/cmd/wails@latest
 16	fi
 17	
 18	# 检查 Go 模块依赖
 19	echo " 检查 Go 依赖..."
 20	go mod tidy
 21	
 22	# 检查前端依赖
 23	if [ -d "frontend/node_modules" ]; then
 24	 echo "✅ 前端依赖已安装"
 25	else
 26	 echo " 安装前端依赖需要 Node.js，请手动运行: cd frontend && npm install"
 27	fi
