 115	// isTextContent 判断是否为文本内容
 116	func (rd *ResponseDecoder) isTextContent(data []byte, contentType string) bool {
 117		// 根据Content-Type判断
 118		if strings.Contains(contentType, "text/") ||
 119			strings.Contains(contentType, "application/json") ||
 120			strings.Contains(contentType, "application/xml") ||
 121			strings.Contains(contentType, "application/javascript") ||
 122			strings.Contains(contentType, "application/x-javascript") ||
 123			strings.Contains(contentType, "text/javascript") ||
 124			strings.Contains(contentType, "application/xhtml+xml") ||
 125			strings.Contains(contentType, "application/rss+xml") ||
 126			strings.Contains(contentType, "application/atom+xml") {
 127			return true
 128		}
 129	
 130		// 如果Content-Type不明确，通过内容判断
 131		if len(data) == 0 {
 132			return true
 133		}
 134	
 135		// 检查是否为有效的UTF-8
 136		if utf8.Valid(data) {
 137			// 检查前1024字节是否主要是可打印字符
 138			checkLen := len(data)
 139			if checkLen > 1024 {
 140				checkLen = 1024
 141			}
 142	
 143			printableCount := 0
 144			for i := 0; i < checkLen; i++ {
 145				b := data[i]
 146				if (b >= 32 && b <= 126) || b == 9 || b == 10 || b == 13 {
 147					printableCount++
 148				}
 149			}
 150	
 151			// 如果80%以上是可打印字符，认为是文本
 152			return float64(printableCount)/float64(checkLen) > 0.8
 153		}
 154	
 155		return false
 156	}
 157	
 158	// generateHexView 生成16进制视图
 159	func (rd *ResponseDecoder) generateHexView(data []byte) string {
 160		if len(data) == 0 {
 161			return ""
 162		}
 163	
 164		var result strings.Builder
 165		const bytesPerLine = 16
 166		maxLines := 1000 // 最多显示1000行，避免过大的内容
 167	
 168		lines := len(data) / bytesPerLine
 169		if len(data)%bytesPerLine != 0 {
 170			lines++
 171		}
 172	
 173		if lines > maxLines {
 174			lines = maxLines
 175		}
 176	
 177		for line := 0; line < lines; line++ {
 178			offset := line * bytesPerLine
 179	
 180			// 写入偏移地址
 181			result.WriteString(fmt.Sprintf("%08x ", offset))
 182	
 183			// 写入16进制数据
 184			for i := 0; i < bytesPerLine; i++ {
 185				if offset+i < len(data) {
 186					result.WriteString(fmt.Sprintf("%02x ", data[offset+i]))
 187				} else {
 188					result.WriteString(" ")
 189				}
 190	
 191				// 在第8个字节后添加额外空格
 192				if i == 7 {
 193					result.WriteString(" ")
 194				}
 195			}
 196	
 197			// 写入ASCII表示
 198			result.WriteString(" |")
 199			for i := 0; i < bytesPerLine && offset+i < len(data); i++ {
 200				b := data[offset+i]
 201				if b >= 32 && b <= 126 {
 202					result.WriteByte(b)
 203				} else {
 204					result.WriteByte('.')
 205				}
 206			}
 207			result.WriteString("|\n")
 208		}
 209	
 210		// 如果内容被截断，添加提示
 211		if len(data) > maxLines*bytesPerLine {
 212			result.WriteString(fmt.Sprintf("\n... (显示前 %d 行，总共 %d 字节)\n", maxLines, len(data)))
 213		}
 214	
 215		return result.String()
 216	}
 217	
 218	// GetContentSummary 获取内容摘要
 219	func (rd *ResponseDecoder) GetContentSummary(response *FlowResponse) string {
 220		if response == nil {
 221			return "无响应"
 222		}
 223	
 224		if len(response.Body) == 0 {
 225			return "空响应"
 226		}
 227	
 228		if response.IsText {
 229			textLen := len(response.DecodedBody)
 230			if textLen > 100 {
 231				preview := string(response.DecodedBody[:100])
 232				return fmt.Sprintf("文本内容 (%d 字节): %s...", textLen, preview)
 233			}
 234			return fmt.Sprintf("文本内容 (%d 字节): %s", textLen, string(response.DecodedBody))
 235		}
 236	
 237		return fmt.Sprintf("二进制内容 (%d 字节)", len(response.Body))
 238	}
