 382		export class Request {
 383		 Method: string;
 384		 URL?: url.URL;
 385		 Proto: string;
 386		 ProtoMajor: number;
 387		 ProtoMinor: number;
 388		 Header: Record<string, string[]>;
 389		 Body: any;
 390		 ContentLength: number;
 391		 TransferEncoding: string[];
 392		 Close: boolean;
 393		 Host: string;
 394		 Form: Record<string, string[]>;
 395		 PostForm: Record<string, string[]>;
 396		 MultipartForm?: multipart.Form;
 397		 Trailer: Record<string, string[]>;
 398		 RemoteAddr: string;
 399		 RequestURI: string;
 400		 TLS?: tls.ConnectionState;
 401		 Response?: Response;
 402		 Pattern: string;
 403		
 404		 static createFrom(source: any = {}) {
 405		 return new Request(source);
 406		 }
 407		
 408		 constructor(source: any = {}) {
 409		 if ('string' === typeof source) source = JSON.parse(source);
 410		 this.Method = source["Method"];
 411		 this.URL = this.convertValues(source["URL"], url.URL);
 412		 this.Proto = source["Proto"];
 413		 this.ProtoMajor = source["ProtoMajor"];
 414		 this.ProtoMinor = source["ProtoMinor"];
 415		 this.Header = source["Header"];
 416		 this.Body = source["Body"];
 417		 this.ContentLength = source["ContentLength"];
 418		 this.TransferEncoding = source["TransferEncoding"];
 419		 this.Close = source["Close"];
 420		 this.Host = source["Host"];
 421		 this.Form = source["Form"];
 422		 this.PostForm = source["PostForm"];
 423		 this.MultipartForm = this.convertValues(source["MultipartForm"], multipart.Form);
 424		 this.Trailer = source["Trailer"];
 425		 this.RemoteAddr = source["RemoteAddr"];
 426		 this.RequestURI = source["RequestURI"];
 427		 this.TLS = this.convertValues(source["TLS"], tls.ConnectionState);
 428		 this.Response = this.convertValues(source["Response"], Response);
 429		 this.Pattern = source["Pattern"];
 430		 }
 431		
 432			convertValues(a: any, classs: any, asMap: boolean = false): any {
 433			 if (!a) {
 434			 return a;
 435			 }
 436			 if (a.slice && a.map) {
 437			 return (a as any[]).map(elem => this.convertValues(elem, classs));
 438			 } else if ("object" === typeof a) {
 439			 if (asMap) {
 440			 for (const key of Object.keys(a)) {
 441			 a[key] = new classs(a[key]);
 442			 }
 443			 return a;
 444			 }
 445			 return new classs(a);
 446			 }
 447			 return a;
 448			}
 449		}
