 782	export namespace tls {
 783		
 784		export class ConnectionState {
 785		 Version: number;
 786		 HandshakeComplete: boolean;
 787		 DidResume: boolean;
 788		 CipherSuite: number;
 789		 NegotiatedProtocol: string;
 790		 NegotiatedProtocolIsMutual: boolean;
 791		 ServerName: string;
 792		 PeerCertificates: x509.Certificate[];
 793		 VerifiedChains: x509.Certificate[][];
 794		 SignedCertificateTimestamps: number[][];
 795		 OCSPResponse: number[];
 796		 TLSUnique: number[];
 797		 ECHAccepted: boolean;
 798		
 799		 static createFrom(source: any = {}) {
 800		 return new ConnectionState(source);
 801		 }
 802		
 803		 constructor(source: any = {}) {
 804		 if ('string' === typeof source) source = JSON.parse(source);
 805		 this.Version = source["Version"];
 806		 this.HandshakeComplete = source["HandshakeComplete"];
 807		 this.DidResume = source["DidResume"];
 808		 this.CipherSuite = source["CipherSuite"];
