 17	// RequestInterceptor 请求拦截器接口
 18	type RequestInterceptor interface {
 19		InterceptRequest(flow *Flow, w http.ResponseWriter, r *http.Request) (handled bool, err error)
 20	}
 21	
 22	// ResponseInterceptor 响应拦截器接口
 23	type ResponseInterceptor interface {
 24		InterceptResponse(flow *Flow, resp *http.Response) (modified *http.Response, err error)
 25	}
 26	
 27	// ProxyServer 代理服务器
 28	type ProxyServer struct {
 29		port int
 30		certManager *certmanager.CertManager
 31		requestInterceptors []RequestInterceptor
 32		responseInterceptors []ResponseInterceptor
 33		server *http.Server
 34		flows map[string]*Flow
 35		flowsMutex sync.RWMutex
 36		flowHandler func(*Flow)
 37		running bool
 38	}
 39	
 40	// NewProxyServer 创建新的代理服务器
 41	func NewProxyServer(port int, certManager *certmanager.CertManager) *ProxyServer {
 42		return &ProxyServer{
 43			port: port,
 44			certManager: certManager,
 45			requestInterceptors: make([]RequestInterceptor, 0),
 46			responseInterceptors: make([]ResponseInterceptor, 0),
 47			flows: make(map[string]*Flow),
 48			running: false,
 49		}
 50	}
 51	
 52	// AddRequestInterceptor 添加请求拦截器
 53	func (ps *ProxyServer) AddRequestInterceptor(interceptor RequestInterceptor) {
 54		ps.requestInterceptors = append(ps.requestInterceptors, interceptor)
 55	}
 56	
 57	// AddResponseInterceptor 添加响应拦截器
 58	func (ps *ProxyServer) AddResponseInterceptor(interceptor ResponseInterceptor) {
 59		ps.responseInterceptors = append(ps.responseInterceptors, interceptor)
 60	}
 61	
 62	// SetFlowHandler 设置流量处理回调函数
 63	func (ps *ProxyServer) SetFlowHandler(handler func(*Flow)) {
 64		ps.flowHandler = handler
 65	}
 66	
 67	// Start 启动代理服务器
 68	func (ps *ProxyServer) Start() error {
 69		if ps.running {
 70			return fmt.Errorf("proxy server is already running")
 71		}
 72	
 73		ps.server = &http.Server{
 74			Addr: fmt.Sprintf(":%d", ps.port),
 75			Handler: ps,
 76		}
 77	
 78		ps.running = true
 79	
 80		go func() {
 81			if err := ps.server.ListenAndServe(); err != nil && err != http.ErrServerClosed {
 82				fmt.Printf("Proxy server error: %v\n", err)
 83			}
 84		}()
 85	
 86		return nil
 87	}
 88	
 89	// Stop 停止代理服务器
 90	func (ps *ProxyServer) Stop() error {
 91		if !ps.running {
 92			return nil
 93		}
 94	
 95		ps.running = false
 96		if ps.server != nil {
 97			return ps.server.Close()
 98		}
 99		return nil
 100	}
 101	
 102	// ServeHTTP 实现 http.Handler 接口
 103	func (ps *ProxyServer) ServeHTTP(w http.ResponseWriter, r *http.Request) {
 104		if r.Method == http.MethodConnect {
 105			ps.handleConnect(w, r)
 106		} else {
 107			ps.handleHTTP(w, r)
 108		}
 109	}
 110	
 111	// handleHTTP 处理普通HTTP请求
 112	func (ps *ProxyServer) handleHTTP(w http.ResponseWriter, r *http.Request) {
 113		// 生成Flow ID
 114		flowID := ps.generateFlowID()
 115		flow := NewFlow(flowID, r)
 116	
 117		// 读取请求体
 118		if r.Body != nil {
 119			body, err := io.ReadAll(r.Body)
 120			if err == nil {
 121				flow.SetRequestBody(body)
 122			}
 123			r.Body.Close()
 124		}
 125	
 126		// 执行请求拦截器
 127		for _, interceptor := range ps.requestInterceptors {
 128			handled, err := interceptor.InterceptRequest(flow, w, r)
 129			if err != nil {
 130				http.Error(w, err.Error(), http.StatusInternalServerError)
 131				return
 132			}
 133			if handled {
 134				// 请求已被拦截器处理，直接返回
 135				ps.addFlow(flow)
 136				return
 137			}
 138		}
 139	
 140		// 创建新的请求
 141		targetURL := r.URL
 142		if !targetURL.IsAbs() {
 143			targetURL = &url.URL{
 144				Scheme: "http",
 145				Host: r.Host,
 146				Path: r.URL.Path,
 147				RawQuery: r.URL.RawQuery,
 148			}
 149		}
 150	
 151		proxyReq, err := http.NewRequest(r.Method, targetURL.String(), strings.NewReader(string(flow.Request.Body)))
 152		if err != nil {
 153			http.Error(w, err.Error(), http.StatusInternalServerError)
 154			return
 155		}
 156	
 157		// 复制请求头
 158		for name, values := range r.Header {
 159			for _, value := range values {
 160				proxyReq.Header.Add(name, value)
 161			}
 162		}
 163	
 164		// 发送请求
 165		client := &http.Client{
 166			Timeout: 30 * time.Second,
 167		}
 168	
 169		resp, err := client.Do(proxyReq)
 170		if err != nil {
 171			http.Error(w, err.Error(), http.StatusBadGateway)
 172			return
 173		}
 174		defer resp.Body.Close()
 175	
 176		// 读取响应体
 177		respBody, err := io.ReadAll(resp.Body)
 178		if err != nil {
 179			http.Error(w, err.Error(), http.StatusInternalServerError)
 180			return
 181		}
 182	
 183		// 设置响应信息
 184		flow.SetResponse(resp, respBody)
 185	
 186		// 执行响应拦截器
 187		modifiedResp := resp
 188		for _, interceptor := range ps.responseInterceptors {
 189			modifiedResp, err = interceptor.InterceptResponse(flow, modifiedResp)
 190			if err != nil {
 191				http.Error(w, err.Error(), http.StatusInternalServerError)
 192				return
 193			}
 194			if modifiedResp == nil {
 195				modifiedResp = resp // 如果拦截器返回nil，使用原始响应
 196			}
 197		}
 198	
 199		// 复制响应头到客户端
 200		for name, values := range modifiedResp.Header {
 201			for _, value := range values {
 202				w.Header().Add(name, value)
 203			}
 204		}
 205	
 206		w.WriteHeader(modifiedResp.StatusCode)
 207	
 208		// 如果响应被修改，需要重新读取响应体
 209		if modifiedResp != resp && modifiedResp.Body != nil {
 210			modifiedBody, err := io.ReadAll(modifiedResp.Body)
 211			if err == nil {
 212				w.Write(modifiedBody)
 213			} else {
 214				w.Write(respBody)
 215			}
 216		} else {
 217			w.Write(respBody)
 218		}
 219	
 220		// 存储并通知Flow
 221		ps.addFlow(flow)
 222	}
 223	
 224	// handleConnect 处理HTTPS CONNECT请求
 225	func (ps *ProxyServer) handleConnect(w http.ResponseWriter, r *http.Request) {
 226		// 响应200 OK
 227		w.WriteHeader(http.StatusOK)
 228	
 229		// 获取底层连接
 230		hijacker, ok := w.(http.Hijacker)
 231		if !ok {
 232			http.Error(w, "Hijacking not supported", http.StatusInternalServerError)
 233			return
 234		}
 235	
 236		clientConn, _, err := hijacker.Hijack()
 237		if err != nil {
 238			http.Error(w, err.Error(), http.StatusInternalServerError)
 239			return
 240		}
 241		defer clientConn.Close()
 242	
 243		// 获取目标主机名
 244		host := r.Host
 245		if !strings.Contains(host, ":") {
 246			host += ":443"
 247		}
 248	
 249		hostname := strings.Split(host, ":")[0]
 250	
 251		// 获取服务器证书
 252		cert, err := ps.certManager.GenerateServerCert(hostname)
 253		if err != nil {
 254			fmt.Printf("Failed to generate certificate for %s: %v\n", hostname, err)
 255			return
 256		}
 257	
 258		// 创建TLS配置
 259		tlsConfig := &tls.Config{
 260			Certificates: []tls.Certificate{*cert},
 261			ServerName: hostname,
 262			MinVersion: tls.VersionTLS12,
 263			MaxVersion: tls.VersionTLS13,
 264			CipherSuites: []uint16{
 265				tls.TLS_ECDHE_RSA_WITH_AES_256_GCM_SHA384,
 266				tls.TLS_ECDHE_RSA_WITH_CHACHA20_POLY1305,
 267				tls.TLS_ECDHE_RSA_WITH_AES_128_GCM_SHA256,
 268				tls.TLS_RSA_WITH_AES_256_GCM_SHA384,
 269				tls.TLS_RSA_WITH_AES_128_GCM_SHA256,
 270			},
 271			PreferServerCipherSuites: true,
 272		}
 273	
 274		// 与客户端建立TLS连接
 275		tlsConn := tls.Server(clientConn, tlsConfig)
 276		if err := tlsConn.Handshake(); err != nil {
 277			fmt.Printf("TLS handshake failed for %s: %v\n", hostname, err)
 278			fmt.Printf("Certificate details: Subject=%s, DNSNames=%v\n",
 279				cert.Leaf.Subject.CommonName, cert.Leaf.DNSNames)
 280			return
 281		}
 282	
 283		fmt.Printf("TLS handshake successful for %s\n", hostname)
 284	
 285		// 开始处理HTTPS流量
 286		ps.handleHTTPS(tlsConn, host)
 287	}
 288	
 289	// addFlow 添加Flow到存储并通知处理器
 290	func (ps *ProxyServer) addFlow(flow *Flow) {
 291		fmt.Printf(" Adding flow: %s %s %s (Status: %d)\n", flow.Method, flow.URL, flow.Domain, flow.StatusCode)
 292	
 293		ps.flowsMutex.Lock()
 294		ps.flows[flow.ID] = flow
 295		ps.flowsMutex.Unlock()
 296	
 297		if ps.flowHandler != nil {
 298			fmt.Printf(" Notifying flow handler for: %s\n", flow.URL)
 299			ps.flowHandler(flow)
 300		} else {
 301			fmt.Printf("⚠️ No flow handler registered!\n")
 302		}
 303	}
 304	
 305	// GetFlows 获取所有Flow
 306	func (ps *ProxyServer) GetFlows() []*Flow {
 307		ps.flowsMutex.RLock()
 308		defer ps.flowsMutex.RUnlock()
 309	
 310		flows := make([]*Flow, 0, len(ps.flows))
 311		for _, flow := range ps.flows {
 312			flows = append(flows, flow)
 313		}
 314		return flows
 315	}
 316	
 317	// GetFlow 根据ID获取单个Flow
 318	func (ps *ProxyServer) GetFlow(flowID string) (*Flow, bool) {
 319		ps.flowsMutex.RLock()
 320		defer ps.flowsMutex.RUnlock()
 321	
 322		flow, exists := ps.flows[flowID]
 323		return flow, exists
 324	}
 325	
 326	// ClearFlows 清空所有Flow
 327	func (ps *ProxyServer) ClearFlows() {
 328		ps.flowsMutex.Lock()
 329		defer ps.flowsMutex.Unlock()
 330		ps.flows = make(map[string]*Flow)
 331	}
 332	
 333	// generateFlowID 生成Flow ID
 334	func (ps *ProxyServer) generateFlowID() string {
 335		return fmt.Sprintf("flow_%d", time.Now().UnixNano())
 336	}
 337	
 338	// PinFlow 钉住/取消钉住流量
 339	func (ps *ProxyServer) PinFlow(flowID string) error {
 340		ps.flowsMutex.Lock()
 341		defer ps.flowsMutex.Unlock()
 342	
 343		flow, exists := ps.flows[flowID]
 344		if !exists {
 345			return fmt.Errorf("flow not found: %s", flowID)
 346		}
 347	
 348		flow.IsPinned = !flow.IsPinned
 349		return nil
 350	}
 351	
 352	// GetPinnedFlows 获取所有钉住的流量
 353	func (ps *ProxyServer) GetPinnedFlows() []*Flow {
 354		ps.flowsMutex.RLock()
 355		defer ps.flowsMutex.RUnlock()
 356	
 357		var pinnedFlows []*Flow
 358		for _, flow := range ps.flows {
 359			if flow.IsPinned {
 360				pinnedFlows = append(pinnedFlows, flow)
 361			}
 362		}
 363		return pinnedFlows
 364	}
 365	
 366	// FilterFlows 根据条件过滤流量
 367	func (ps *ProxyServer) FilterFlows(filter func(*Flow) bool) []*Flow {
 368		ps.flowsMutex.RLock()
 369		defer ps.flowsMutex.RUnlock()
 370	
 371		var filteredFlows []*Flow
 372		for _, flow := range ps.flows {
 373			if filter(flow) {
 374				filteredFlows = append(filteredFlows, flow)
 375			}
 376		}
 377		return filteredFlows
 378	}
 379	
 380	// GetFlowByID 根据ID获取流量
 381	func (ps *ProxyServer) GetFlowByID(flowID string) (*Flow, error) {
 382		ps.flowsMutex.RLock()
 383		defer ps.flowsMutex.RUnlock()
 384	
 385		flow, exists := ps.flows[flowID]
 386		if !exists {
 387			return nil, fmt.Errorf("flow not found: %s", flowID)
 388		}
 389		return flow, nil
 390	}
