 392	// handleHTTPS 处理HTTPS流量
 393	func (ps *ProxyServer) handleHTTPS(tlsConn *tls.Conn, targetHost string) {
 394		defer tlsConn.Close()
 395	
 396		fmt.Printf("Starting HTTPS handler for %s\n", targetHost)
 397	
 398		// 创建HTTP服务器来处理解密后的HTTPS请求
 399		server := &http.Server{
 400			Handler: http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
 401				fmt.Printf(" Received HTTPS request: %s %s from %s\n", r.Method, r.URL.Path, targetHost)
 402				fmt.Printf(" Request headers: %v\n", r.Header)
 403	
 404				// 设置完整的URL
 405				r.URL.Scheme = "https"
 406				r.URL.Host = targetHost
 407	
 408				// 处理为普通HTTP请求
 409				fmt.Printf(" Calling handleHTTP for HTTPS request\n")
 410				ps.handleHTTP(w, r)
 411				fmt.Printf(" handleHTTP completed for HTTPS request\n")
 412			}),
 413			ReadTimeout: 30 * time.Second,
 414			WriteTimeout: 30 * time.Second,
 415		}
 416	
 417		// 使用TLS连接处理HTTP请求
 418		listener := &singleConnListener{conn: tlsConn}
 419		err := server.Serve(listener)
 420		if err != nil && err != io.EOF && !strings.Contains(err.Error(), "use of closed network connection") {
 421			fmt.Printf("HTTPS server error for %s: %v\n", targetHost, err)
 422		}
 423	
 424		fmt.Printf("HTTPS handler finished for %s\n", targetHost)
 425	}
 426	
 427	
 428	
 429	// singleConnListener 单连接监听器
 430	type singleConnListener struct {
 431		conn net.Conn
 432		once sync.Once
 433		closed chan struct{}
 434	}
 435	
 436	func (l *singleConnListener) Accept() (net.Conn, error) {
 437		var conn net.Conn
 438	
 439		l.once.Do(func() {
 440			if l.closed == nil {
 441				l.closed = make(chan struct{})
 442			}
 443			conn = l.conn
 444		})
 445	
 446		if conn != nil {
 447			return conn, nil
 448		}
 449	
 450		// 等待关闭信号
 451		<-l.closed
 452		return nil, io.EOF
 453	}
 454	
 455	func (l *singleConnListener) Close() error {
 456		if l.closed != nil {
 457			close(l.closed)
 458		}
 459		if l.conn != nil {
 460			return l.conn.Close()
 461		}
 462		return nil
 463	}
 464	
 465	func (l *singleConnListener) Addr() net.Addr {
 466		if l.conn != nil {
 467			return l.conn.LocalAddr()
 468		}
 469		return nil
 470	}
