 20	// Flow 表示一个完整的HTTP请求/响应流
 21	type Flow struct {
 22		ID string `json:"id"`
 23		URL string `json:"url"`
 24		Method string `json:"method"`
 25		StatusCode int `json:"statusCode"`
 26		Client string `json:"client"`
 27		Domain string `json:"domain"`
 28		Path string `json:"path"`
 29		Scheme string `json:"scheme"`
 30		StartTime time.Time `json:"startTime"`
 31		EndTime time.Time `json:"endTime"`
 32		Duration time.Duration `json:"duration"`
 33		RequestSize int64 `json:"requestSize"`
 34		ResponseSize int64 `json:"responseSize"`
 35		Request *FlowRequest `json:"request"`
 36		Response *FlowResponse `json:"response"`
 37		IsPinned bool `json:"isPinned"`
 38		IsBlocked bool `json:"isBlocked"`
 39		ContentType string `json:"contentType"`
 40		Tags []string `json:"tags"`
 41		ScriptExecutions []ScriptExecution `json:"scriptExecutions,omitempty"`
 42	}
