 141	// AddTag 添加标签
 142	func (f *Flow) AddTag(tag string) {
 143		for _, existingTag := range f.Tags {
 144			if existingTag == tag {
 145				return // 标签已存在
 146			}
 147		}
 148		f.Tags = append(f.Tags, tag)
 149	}
 150	
 151	// RemoveTag 移除标签
 152	func (f *Flow) RemoveTag(tag string) {
 153		for i, existingTag := range f.Tags {
 154			if existingTag == tag {
 155				f.Tags = append(f.Tags[:i], f.Tags[i+1:]...)
 156				return
 157			}
 158		}
 159	}
