 10	// MapLocalInterceptor Map Local拦截器
 11	type MapLocalInterceptor struct {
 12		manager *MapLocalManager
 13	}
 14	
 15	// NewMapLocalInterceptor 创建Map Local拦截器
 16	func NewMapLocalInterceptor(manager *MapLocalManager) *MapLocalInterceptor {
 17		return &MapLocalInterceptor{
 18			manager: manager,
 19		}
 20	}
 21	
 22	// InterceptRequest 拦截请求
 23	func (mli *MapLocalInterceptor) InterceptRequest(flow *proxycore.Flow, w http.ResponseWriter, r *http.Request) (bool, error) {
 24		rule, err := mli.manager.MatchRule(flow.URL)
 25		if err != nil {
 26			return false, err
 27		}
 28		
 29		if rule != nil {
 30			// 处理Map Local
 31			err = mli.manager.HandleMapLocal(w, r, rule)
 32			if err != nil {
 33				return false, err
 34			}
 35			
 36			// 标记Flow为Map Local处理
 37			flow.AddTag("map-local")
 38			return true, nil
 39		}
 40		
 41		return false, nil
 42	}
 43	
 44	// BreakpointInterceptor 断点拦截器
 45	type BreakpointInterceptor struct {
 46		manager *BreakpointManager
 47		eventHandler func(session *BreakpointSession)
 48	}
 49	
 50	// NewBreakpointInterceptor 创建断点拦截器
 51	func NewBreakpointInterceptor(manager *BreakpointManager) *BreakpointInterceptor {
 52		return &BreakpointInterceptor{
 53			manager: manager,
 54		}
 55	}
 56	
 57	// SetEventHandler 设置事件处理器
 58	func (bi *BreakpointInterceptor) SetEventHandler(handler func(session *BreakpointSession)) {
 59		bi.eventHandler = handler
 60		bi.manager.SetEventHandler(handler)
 61	}
 62	
 63	// InterceptRequest 拦截请求
 64	func (bi *BreakpointInterceptor) InterceptRequest(flow *proxycore.Flow, w http.ResponseWriter, r *http.Request) (bool, error) {
 65		session, hasBreakpoint := bi.manager.CheckBreakpoint(flow, "request")
 66		if !hasBreakpoint {
 67			return false, nil
 68		}
 69		
 70		// 等待断点恢复
 71		_, err := bi.manager.WaitForBreakpoint(session, 5*time.Minute)
 72		if err != nil {
 73			return false, err
 74		}
 75		
 76		// 如果有修改的请求，使用修改后的请求
 77		if session.ModifiedRequest != nil {
 78			*r = *session.ModifiedRequest
 79		}
 80		
 81		flow.AddTag("breakpoint-request")
 82		return false, nil // 继续处理请求
 83	}
 84	
 85	// InterceptResponse 拦截响应
 86	func (bi *BreakpointInterceptor) InterceptResponse(flow *proxycore.Flow, resp *http.Response) (*http.Response, error) {
 87		session, hasBreakpoint := bi.manager.CheckBreakpoint(flow, "response")
 88		if !hasBreakpoint {
 89			return resp, nil
 90		}
 91		
 92		// 等待断点恢复
 93		modifiedResp, err := bi.manager.WaitForBreakpoint(session, 5*time.Minute)
 94		if err != nil {
 95			return resp, err
 96		}
 97		
 98		flow.AddTag("breakpoint-response")
 99	
 100		if modifiedResp != nil {
 101			return modifiedResp, nil
 102		}
 103	
 104		return resp, nil
 105	}
 106	
 107	// ScriptInterceptor 脚本拦截器
 108	type ScriptInterceptor struct {
 109		manager *ScriptManager
 110	}
 111	
 112	// NewScriptInterceptor 创建脚本拦截器
 113	func NewScriptInterceptor(manager *ScriptManager) *ScriptInterceptor {
 114		return &ScriptInterceptor{
 115			manager: manager,
 116		}
 117	}
 118	
 119	// InterceptRequest 拦截请求
 120	func (si *ScriptInterceptor) InterceptRequest(flow *proxycore.Flow, w http.ResponseWriter, r *http.Request) (bool, error) {
 121		err := si.manager.ExecuteRequestScripts(flow)
 122		if err != nil {
 123			return false, err
 124		}
 125	
 126		return false, nil // 继续处理请求
 127	}
 128	
 129	// InterceptResponse 拦截响应
 130	func (si *ScriptInterceptor) InterceptResponse(flow *proxycore.Flow, resp *http.Response) (*http.Response, error) {
 131		err := si.manager.ExecuteResponseScripts(flow)
 132		if err != nil {
 133			return resp, err
 134		}
 135	
 136		return resp, nil
 137	}
