 44	// FlowRequest 表示HTTP请求
 45	type FlowRequest struct {
 46		Method string `json:"method"`
 47		URL string `json:"url"`
 48		Headers map[string]string `json:"headers"`
 49		Body []byte `json:"body"`
 50		Raw string `json:"raw"`
 51	}
