 53	// FlowResponse 表示HTTP响应
 54	type FlowResponse struct {
 55		StatusCode int `json:"statusCode"`
 56		Status string `json:"status"`
 57		Headers map[string]string `json:"headers"`
 58		Body []byte `json:"body"` // 原始响应体
