 68	// NewFlow 创建新的Flow对象
 69	func NewFlow(id string, req *http.Request) *Flow {
 70		scheme := "http"
 71		if req.TLS != nil {
 72			scheme = "https"
 73		}
 74	
 75		flow := &Flow{
 76			ID: id,
 77			URL: req.URL.String(),
 78			Method: req.Method,
 79			Client: req.RemoteAddr,
 80			Domain: req.Host,
 81			Path: req.URL.Path,
 82			Scheme: scheme,
 83			StartTime: time.Now(),
 84			Tags: make([]string, 0),
 85			Request: &FlowRequest{
 86				Method: req.Method,
 87				URL: req.URL.String(),
 88				Headers: make(map[string]string),
 89			},
 90		}
 91	
 92		// 复制请求头
 93		for name, values := range req.Header {
 94			if len(values) > 0 {
 95				flow.Request.Headers[name] = values[0]
 96			}
 97		}
 98	
 99		// 设置内容类型
 100		if contentType := req.Header.Get("Content-Type"); contentType != "" {
 101			flow.ContentType = contentType
 102		}
 103	
 104		return flow
 105	}
 106	
 107	// SetResponse 设置响应信息
 108	func (f *Flow) SetResponse(resp *http.Response, body []byte) {
 109		f.EndTime = time.Now()
 110		f.Duration = f.EndTime.Sub(f.StartTime)
 111		f.StatusCode = resp.StatusCode
 112		f.ResponseSize = int64(len(body))
 113	
 114		f.Response = &FlowResponse{
 115			StatusCode: resp.StatusCode,
 116			Status: resp.Status,
 117			Headers: make(map[string]string),
 118			Body: body,
 119		}
 120	
 121		// 复制响应头
 122		for name, values := range resp.Header {
 123			if len(values) > 0 {
 124				f.Response.Headers[name] = values[0]
 125			}
 126		}
 127	
 128		// 更新内容类型（如果响应中有）
 129		if contentType := resp.Header.Get("Content-Type"); contentType != "" && f.ContentType == "" {
 130			f.ContentType = contentType
 131		}
 132	
 133		// 自动解码响应体
 134		decoder := NewResponseDecoder()
 135		if err := decoder.DecodeResponse(f.Response); err != nil {
 136			// 解码失败，记录错误但不影响正常流程
 137			fmt.Printf("Failed to decode response for %s: %v\n", f.URL, err)
 138		}
 139	}
 140	
 141	// AddTag 添加标签
 142	func (f *Flow) AddTag(tag string) {
 143		for _, existingTag := range f.Tags {
 144			if existingTag == tag {
 145				return // 标签已存在
 146			}
 147		}
 148		f.Tags = append(f.Tags, tag)
 149	}
 150	
 151	// RemoveTag 移除标签
 152	func (f *Flow) RemoveTag(tag string) {
 153		for i, existingTag := range f.Tags {
 154			if existingTag == tag {
 155				f.Tags = append(f.Tags[:i], f.Tags[i+1:]...)
 156				return
 157			}
 158		}
 159	}
 160	
 161	// SetRequestBody 设置请求体
 162	func (f *Flow) SetRequestBody(body []byte) {
 163		f.Request.Body = body
 164		f.RequestSize = int64(len(body))
 165	}
