 32	// NewApp creates a new App application struct
 33	func NewApp() *App {
 34		systemManager := system.NewSystemManager()
 35	
 36		// 加载配置
 37		cfg, err := config.LoadConfig(systemManager.GetConfigDir())
 38		if err != nil {
 39			fmt.Printf("Failed to load config, using defaults: %v\n", err)
 40			cfg = config.DefaultConfig()
 41			cfg.ConfigDir = systemManager.GetConfigDir()
 42		}
 43	
 44		// 初始化日志
 45		if err := logger.InitLogger(cfg.ConfigDir, cfg.LogLevel); err != nil {
 46			fmt.Printf("Failed to initialize logger: %v\n", err)
 47		}
 48	
 49		// 初始化数据库
 50		database, err := storage.NewDatabase()
 51		if err != nil {
 52			fmt.Printf("Failed to initialize database: %v\n", err)
 53			// 继续运行，但功能会受限
 54		}
 55	
 56		certManager := certmanager.NewCertManager(cfg.ConfigDir)
 57		featureManager := features.NewFeatureManager(database)
 58		exportService := export.NewExportService()
 59	
 60		app := &App{
 61			config: cfg,
 62			certManager: certManager,
 63			systemManager: systemManager,
 64			featureManager: featureManager,
 65			exportService: exportService,
 66			database: database,
 67			isRunning: false,
 68		}
 69	
 70		return app
 71	}
