 73	// startup is called when the app starts. The context is saved
 74	// so we can call the runtime methods
 75	func (a *App) startup(ctx context.Context) {
 76		a.ctx = ctx
 77	
 78		// 初始化证书管理器
 79		if err := a.certManager.InitCA(); err != nil {
 80			logger.Error("Failed to initialize CA: %v", err)
 81			fmt.Printf("Failed to initialize CA: %v\n", err)
 82		}
 83	
 84		// 创建代理服务器
 85		a.proxyServer = proxycore.NewProxyServer(a.config.ProxyPort, a.certManager)
 86	
 87		// 设置拦截器
 88		allowBlockInterceptor := features.NewAllowBlockInterceptor(a.featureManager.AllowBlock)
 89		mapLocalInterceptor := features.NewMapLocalInterceptor(a.featureManager.MapLocal)
 90		breakpointInterceptor := features.NewBreakpointInterceptor(a.featureManager.Breakpoint)
 91		scriptInterceptor := features.NewScriptInterceptor(a.featureManager.Scripting)
 92	
 93		// 设置断点事件处理器
 94		breakpointInterceptor.SetEventHandler(func(session *features.BreakpointSession) {
 95			// 通过Wails事件系统发送断点事件到前端
 96			runtime.EventsEmit(ctx, "breakpoint-hit", session)
 97		})
 98	
 99		// 创建更多拦截器
 100		upstreamInterceptor := features.NewUpstreamInterceptor(a.featureManager.Upstream)
 101		reverseProxyInterceptor := features.NewReverseProxyInterceptor(a.featureManager.ReverseProxy)
 102	
 103		// 按顺序添加拦截器（顺序很重要）
 104		a.proxyServer.AddRequestInterceptor(allowBlockInterceptor) // 首先检查允许/阻止
 105		a.proxyServer.AddRequestInterceptor(reverseProxyInterceptor) // 然后检查反向代理
 106		a.proxyServer.AddRequestInterceptor(upstreamInterceptor) // 然后检查上游代理
 107		a.proxyServer.AddRequestInterceptor(mapLocalInterceptor) // 然后检查Map Local
 108		a.proxyServer.AddRequestInterceptor(breakpointInterceptor) // 然后检查断点
 109		a.proxyServer.AddRequestInterceptor(scriptInterceptor) // 最后执行脚本
 110	
 111		a.proxyServer.AddResponseInterceptor(breakpointInterceptor) // 响应断点
 112		a.proxyServer.AddResponseInterceptor(scriptInterceptor) // 响应脚本
 113	
 114		// 设置流量处理回调
 115		a.proxyServer.SetFlowHandler(func(flow *proxycore.Flow) {
 116			// 通过Wails事件系统发送新的流量到前端
 117			runtime.EventsEmit(ctx, "new-flow", flow)
 118		})
 119	}
