 11	)
 12	
 13	//go:embed all:frontend/dist
 14	var assets embed.FS
 15	
 16	func main() {
 17		// Create an instance of the app structure
 18		app := NewApp()
 19	
 20		// Create application with options
 21		err := wails.Run(&options.App{
 22			Title: "ProxyWoman",
 23			Width: 1200,
 24			Height: 800,
 25			AssetServer: &assetserver.Options{
 26				Assets: assets,
 27			},
 28			BackgroundColour: &options.RGBA{R: 30, G: 30, B: 30, A: 1},
 29			OnStartup: app.startup,
 30			OnShutdown: app.Shutdown,
 31			Bind: []interface{}{
 32				app,
 33			},
 34		})
 35	
 36		if err != nil {
 37			println("Error:", err.Error())
 38		}
 39	}
