Path: cmd/cli/main.go
...
 28	
 29	func main() {
 30		if len(os.Args) < 2 {
 31			printUsage()
 32			os.Exit(1)
 33		}
 34	
 35		command := os.Args[1]
 36		args := os.Args[2:]
 37	
 38		cli := &CLI{}
 39		cli.init()
 40	
 41		switch command {
 42		case "start":
 43			cli.startProxy(args)
 44		case "stop":
 45			cli.stopProxy(args)
 46		case "status":
 47			cli.showStatus(args)
 48		case "cert":
 49			cli.manageCert(args)
 50		case "test-cert":
 51			cli.testCert(args)
 52		case "export":
 53			cli.exportHAR(args)
 54		case "import":
 55			cli.importHAR(args)
 56		case "rules":
 57			cli.manageRules(args)
 58		case "scripts":
 59			cli.manageScripts(args)
 60		case "config":
 61			cli.manageConfig(args)
 62		case "help":
 63			printUsage()
 64		default:
 65			fmt.Printf("Unknown command: %s\n", command)
 66			printUsage()
 67			os.Exit(1)
 68		}
 69	}
