 53	// 创建可写的流量存储
 54	export const flows = writable<Flow[]>([]);
 55	
 56	// 创建过滤器存储
 57	export const filterText = writable<string>('');
 58	
 59	// 创建过滤后的流量派生存储
 60	export const filteredFlows = derived(
 61	 [flows, filterText],
 62	 ([$flows, $filterText]) => {
 63	 if (!$filterText) {
 64	 return $flows;
 65	 }
 66	 
 67	 const filter = $filterText.toLowerCase();
 68	 return $flows.filter(flow => 
 69	 flow.url.toLowerCase().includes(filter) ||
 70	 flow.method.toLowerCase().includes(filter) ||
 71	 flow.domain.toLowerCase().includes(filter) ||
 72	 flow.statusCode.toString().includes(filter)
 73	 );
 74	 }
 75	);
 76	
 77	// 流量操作函数
 78	export const flowActions = {
 79	 // 添加新流量
 80	 addFlow: (flow: Flow) => {
 81	 // 检测应用信息
 82	 const userAgent = flow.request?.headers?.['User-Agent'] || '';
 83	 const appInfo = detectApp(flow.domain, userAgent, flow.request?.headers);
 84	
 85	 // 添加应用信息到流量
 86	 const enrichedFlow = {
 87	 ...flow,
 88	 appName: appInfo.name,
 89	 appIcon: appInfo.icon,
 90	 appCategory: appInfo.category
 91	 };
 92	
 93	 flows.update(currentFlows => [enrichedFlow, ...currentFlows]);
 94	 },
 95	
 96	 // 清空所有流量
 97	 clearFlows: () => {
 98	 flows.set([]);
 99	 },
 100	
 101	 // 切换流量的固定状态
 102	 togglePin: (flowId: string) => {
 103	 flows.update(currentFlows => 
 104	 currentFlows.map(flow => 
 105	 flow.id === flowId 
 106	 ? { ...flow, isPinned: !flow.isPinned }
 107	 : flow
 108	 )
 109	 );
 110	 },
 111	
 112	 // 设置过滤文本
 113	 setFilter: (text: string) => {
 114	 filterText.set(text);
 115	 }
 116	};
