 37		if err != nil {
 38			// 解码失败，使用原始内容
 39			decodedBody = response.Body
 40		}
 41		response.DecodedBody = decodedBody
 42	
 43		// 判断内容类型
 44		response.IsText = rd.isTextContent(decodedBody, contentType)
 45		response.IsBinary = !response.IsText
 46	
 47		// 生成16进制视图（对于二进制内容或大文件）
 48		if response.IsBinary || len(decodedBody) > 1024*1024 { // 大于1MB的文件
 49			response.HexView = rd.generateHexView(decodedBody)
 50		}
 51	
 52		return nil
 53	}
 54	
 55	// decompressBody 解压响应体
 56	func (rd *ResponseDecoder) decompressBody(body []byte, encoding string) ([]byte, error) {
 57		switch encoding {
 58		case "gzip":
 59			return rd.decompressGzip(body)
 60		case "deflate":
 61			return rd.decompressDeflate(body)
 62		case "br":
 63			return rd.decompressBrotli(body)
 64		default:
 65			// 尝试自动检测压缩格式
 66			if decompressed, err := rd.autoDetectAndDecompress(body); err == nil {
 67				return decompressed, nil
 68			}
 69			return body, nil
 70		}
 71	}
 72	
 73	// decompressGzip 解压Gzip
 74	func (rd *ResponseDecoder) decompressGzip(data []byte) ([]byte, error) {
 75		reader, err := gzip.NewReader(bytes.NewReader(data))
 76		if err != nil {
 77			return nil, err
 78		}
 79		defer reader.Close()
 80	
 81		return io.ReadAll(reader)
 82	}
