 148	 debugLog('Converting array to string, length:', bytes.length);
 149	 const result = new TextDecoder().decode(new Uint8Array(bytes));
 150	 debugLog('Array conversion successful, result length:', result.length);
 151	 return result;
 152	 } catch (error) {
 153	 console.warn('Failed to decode byte array:', error);
 154	 debugDataType(bytes, 'Failed array conversion');
 155	 return String(bytes);
 156	 }
