 1	// 解码工具模块
 2	
 3	export interface DecodingResult {
 4	 success: boolean;
 5	 content: string;
 6	 method: string;
 7	 error?: string;
 8	}
 9	
 10	export interface DecodingAttempt {
 11	 method: string;
 12	 description: string;
 13	 decoder: (content: string) => DecodingResult;
 14	}
 15	
 16	/**
 17	 * Base64解码
 18	 */
 19	export function decodeBase64(content: string): DecodingResult {
 20	 try {
 21	 // 检查是否为有效的Base64格式
 22	 if (!/^[A-Za-z0-9+/]*={0,2}$/.test(content.trim())) {
 23	 return {
 24	 success: false,
 25	 content: content,
 26	 method: 'Base64',
 27	 error: '不是有效的Base64格式'
 28	 };
 29	 }
 30	
 31	 const decoded = atob(content.trim());
 32	 return {
 33	 success: true,
 34	 content: decoded,
 35	 method: 'Base64'
 36	 };
 37	 } catch (error) {
 38	 return {
 39	 success: false,
 40	 content: content,
 41	 method: 'Base64',
 42	 error: error.message
 43	 };
 44	 }
 45	}
...
 145	
 146	/**
 147	 * Hex解码
 148	 */
 149	export function decodeHex(content: string): DecodingResult {
 150	 try {
 151	 // 移除空格和常见的分隔符
 152	 const cleanHex = content.replace(/[\s\-:]/g, '');
 153	 
 154	 // 检查是否为有效的十六进制
 155	 if (!/^[0-9a-fA-F]+$/.test(cleanHex) || cleanHex.length % 2 !== 0) {
 156	 return {
 157	 success: false,
 158	 content: content,
 159	 method: 'Hex',
 160	 error: '不是有效的十六进制格式'
 161	 };
 162	 }
 163	
 164	 let decoded = '';
 165	 for (let i = 0; i < cleanHex.length; i += 2) {
 166	 decoded += String.fromCharCode(parseInt(cleanHex.substr(i, 2), 16));
 167	 }
 168	 
 169	 return {
 170	 success: true,
 171	 content: decoded,
 172	 method: 'Hex'
 173	 };
 174	 } catch (error) {
 175	 return {
 176	 success: false,
 177	 content: content,
 178	 method: 'Hex',
 179	 error: error.message
 180	 };
 181	 }
 182	}
