 296	/**
 297	 * 获取针对特定内容类型的解码尝试列表
 298	 */
 299	export function getDecodingAttempts(contentType: string, url: string = ''): DecodingAttempt[] {
 300	 const attempts: DecodingAttempt[] = [];
 301	
 302	 // 基础解码方法（适用于所有类型）
 303	 attempts.push({
 304	 method: 'Base64',
 305	 description: 'Base64解码',
 306	 decoder: decodeBase64
 307	 });
 308	
 309	 attempts.push({
 310	 method: 'URL',
 311	 description: 'URL解码',
 312	 decoder: decodeURL
 313	 });
 314	
 315	 // 根据内容类型添加特定解码方法
 316	 if (contentType.includes('text/html') || contentType.includes('application/xhtml')) {
 317	 attempts.push({
 318	 method: 'HTML',
 319	 description: 'HTML实体解码',
 320	 decoder: decodeHTML
 321	 });
 322	 }
 323	
 324	 if (contentType.includes('application/json') || contentType.includes('text/javascript')) {
 325	 attempts.push({
 326	 method: 'Unicode',
 327	 description: 'Unicode解码',
 328	 decoder: decodeUnicode
 329	 });
 330	 }
